INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(1,1,1,'Me identifico como un ser vivo que comparte algunas características con otros seres vivos y que se relaciona con ellos en un entorno en el que todos nos desarrollamos.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(2,1,1,'Reconozco en el entorno fenómenos físicos que me afectan y desarrollo habilidades para aproximarme a ellos.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(3,1,1,'Valoro la utilidad de algunos objetos y técnicas desarrollados por el ser humano y reconozco que somos agentes de cambio en el entorno y en la sociedad.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(4,2,1,'Me reconozco como ser social e histórico, miembro de un país con diversas etnias y culturas, con un legado que genera identidad nacional.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(5,2,1,'Reconozco la interacción entre el ser humano y el paisaje en diferentes contextos e identifico las acciones económicas y las consecuencias que resultan de esta relación.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(6,2,1,'Me identifico como un ser humano único, miembro de diversas organizaciones sociales y políticas necesarias para el bienestar y el desarrollo personal y comunitario; reconozco que las normas son acuerdos básicos que buscan la convivencia pacífica en la diversidad.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(7,3,1,'Reconozco significados del número en diferentes contextos (medición, conteo, comparación, codificación, localización entre otros).',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(8,3,1,'Describo, comparo y cuantifico situaciones con números, en diferentes contextos y con diversas representaciones.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(9,3,1,'Describo situaciones que requieren el uso de medidas relativas.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(10,3,1,'Describo situaciones de medición utilizando fracciones comunes.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(11,3,1,'Uso representaciones –principalmente concretas y pictóricas– para explicar el valor de posición en el sistema de numeración decimal.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(12,3,1,'Uso representaciones –principalmente concretas y pictóricas– para realizar equivalencias de un número en las diferentes unidades del sistema decimal.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(13,3,1,'Reconozco propiedades de los números (ser par, ser impar, etc.) y relaciones entre ellos (ser mayor que, ser menor que, ser múltiplo de, ser divisible por, etc.) en diferentes contextos.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(14,3,1,'Resuelvo y formulo problemas en situaciones aditivas de composición y de transformación.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(15,3,1,'Resuelvo y formulo problemas en situaciones de variación proporcional.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(16,3,1,'Uso diversas estrategias de cálculo (especialmente cálculo mental) y de estimación para resolver problemas en situaciones aditivas y multiplicativas.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(17,3,1,'Identifico, si a la luz de los datos de un problema, los resultados obtenidos son o no razonables.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(18,3,1,'Identifico regularidades y propiedades de los números utilizando diferentes instrumentos de cálculo (calculadoras, ábacos, bloques multibase, etc.).',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(19,3,1,'Diferencio atributos y propiedades de objetos tridimensionales.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(20,3,1,'Dibujo y describo cuerpos o figuras tridimensionales en distintas posiciones y tamaños.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(21,3,1,'Reconozco nociones de horizontalidad, verticalidad, paralelismo y perpendicularidad en distintos contextos y su condición relativa con respecto a diferentes sistemas de referencia.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(22,3,1,'Represento el espacio circundante para establecer relaciones espaciales.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(23,3,1,'Reconozco y aplico traslaciones y giros sobre una figura.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(24,3,1,'Reconozco y valoro simetrías en distintos aspectos del arte y el diseño.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(25,3,1,'Reconozco congruencia y semejanza entre figuras (ampliar, reducir).',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(26,3,1,'Realizo construcciones y diseños utilizando cuerpos y figuras geométricas tridimensionales y dibujos o figuras geométricas bidimensionales.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(27,3,1,'Desarrollo habilidades para relacionar dirección, distancia y posición en el espacio.',true);
INSERT INTO public.standards(id,area_id,cycleMEN_id,name,is_active) VALUES(28,3,1,'Reconozco en los objetos propiedades o atributos que se puedan medir (longitud, área, volumen, capacidad, peso y masa) y, en los eventos, su duración.',true);
