INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(940,2,5,'Identifico las funciones que cumplen las oficinas de vigilancia y control del Estado.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(941,2,5,'Identifico mecanismos e instituciones constitucionales que protegen los derechos fundamentales de los ciudadanos y las ciudadanas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(942,6,1,'Comprendo que todos los niños y niñas tenemos derecho a recibir buen trato, cuidado y amor.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(943,6,1,'Reconozco las emociones básicas (alegría, tristeza, rabia, temor) en mí y en las otras personas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(944,6,1,'Expreso mis sentimientos y emociones mediante distintas formas y lenguajes (gestos, palabras, pintura, teatro, juegos, etc).',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(945,6,1,'Reconozco que las acciones se relacionan con las emociones y que puedo aprender a manejar mis emociones para no hacer daño a otras personas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(946,6,1,'Comprendo que mis acciones pueden afectar a la gente cercana y que las acciones de la gente cercana pueden afectarme a mí.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(947,6,1,'Comprendo que nada justifica el maltrato de niñas y niños y que todo maltrato se puede evitar.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(948,6,1,'Identifico las situaciones de maltrato que se dan en mi entorno (conmigo y con otras personas) y sé a quiénes acudir para pedir ayuda y protección.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(949,6,1,'Puedo diferenciar las expresiones verdaderas de cariño de aquellas que pueden maltratarme. (Pido a los adultos que me enseñen a diferenciar las muestras verdaderamente cariñosas de las de abuso sexual o físico y que podamos hablar de esto en la casa y en el salón.)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(950,6,1,'Hago cosas que ayuden a aliviar el malestar de personas cercanas; manifiesto satisfacción al preocuparme por sus necesidades.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(951,6,1,'Comprendo que las normas ayudan a promover el buen trato y evitar el maltrato en el juego y en la vida escolar.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(952,6,1,'Identifico cómo me siento yo o las personas cercanas cuando no recibimos buen trato y expreso empatía, es decir, sentimientos parecidos o compatibles con los de otros. (Estoy triste porque a Juan le pegaron.)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(953,6,1,'Conozco y respeto las reglas básicas del diálogo, como el uso de la palabra y el respeto por la palabra de la otra persona. (Clave: practico lo que he aprendido en otras áreas, sobre la comunicación, los mensajes y la escucha activa.)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(954,6,1,'Conozco y uso estrategias sencillas de resolución pacífica de conflictos. (¿Cómo establecer un acuerdo creativo para usar nuestro único balón en los recreos... sin jugar siempre al mismo juego?)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(955,6,1,'Conozco las señales y las normas básicas de tránsito para desplazarme con seguridad.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(956,6,1,'Me preocupo porque los animales, las plantas y los recursos del medio ambiente reciban buen trato.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(957,6,1,'Expreso mis ideas, sentimientos e intereses en el salón y escucho respetuosamente los de los demás miembros del grupo.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(958,6,1,'Manifiesto mi punto de vista cuando se toman decisiones colectivas en la casa y en la vida escolar.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(959,6,1,'Reconozco que emociones como el temor o la rabia pueden afectar mi participación en clase.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(960,6,1,'Manifiesto desagrado cuando a mí o a alguien del salón no nos escuchan o no nos toman en cuenta y lo expreso... sin agredir.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(961,6,1,'Comprendo qué es una norma y qué es un acuerdo.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(962,6,1,'Entiendo el sentido de las acciones reparadoras, es decir de las acciones que buscan enmendar el daño causado cuando incumplo normas o acuerdos.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(963,6,1,'Colaboro activamente para el logro de metas comunes en mi salón y reconozco la importancia que tienen las normas para lograr esas metas. (Por ejemplo, en nuestro proyecto para la Feria de la Ciencia.)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(964,6,1,'Participo en los procesos de elección de representantes estudiantiles, conociendo bien cada propuesta antes de elegir.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(965,6,1,'Identifico las diferencias y semejanzas de género, aspectos físicos, grupo étnico, origen social, costumbres, gustos, ideas y tantas otras que hay entre las demás personas y yo.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(966,6,1,'Reconozco y acepto la existencia de grupos con diversas características de etnia, edad, género, oficio, lugar, situación socioeconómica, etc.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(967,6,1,'Valoro las semejanzas y diferencias de gente cercana. (¿Qué tal si me detengo a escuchar sus historias de vida?)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(968,6,1,'Identifico las ocasiones en que mis amigos/as o yo hemos hecho sentir mal a alguien excluyéndolo, burlándonos o poniéndole apodos ofensivos.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(969,6,1,'Manifiesto desagrado cuando me excluyen o excluyen a alguien por su género, etnia, condición social y características físicas, y lo digo respetuosamente.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(970,6,1,'Comparo cómo me siento cuando me discriminan o me excluyen... y cómo, cuando me aceptan. Así puedo explicar por qué es importante aceptar a las personas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(971,6,2,'Entiendo que los conflictos son parte de las relaciones, pero que tener conflictos no significa que dejemos de ser amigos o querernos.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(972,6,2,'Conozco la diferencia entre conflicto y agresión y comprendo que la agresión (no los conflictos) es lo que puede hacerle daño a las relaciones.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(973,6,2,'Identifico los puntos de vista de la gente con la que tengo conflictos poniéndome en su lugar.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(974,6,2,'Identifico las ocasiones en que actúo en contra de los derechos de otras personas y comprendo por qué esas acciones vulneran sus derechos.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(975,6,2,'Expongo mis posiciones y escucho las posiciones ajenas, en situaciones de conflicto.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(976,6,2,'Identifico múltiples opciones para manejar mis conflictos y veo las posibles consecuencias de cada opción.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(977,6,2,'Utilizo mecanismos para manejar mi rabia. (Ideas para tranquilizarme: respirar profundo, alejarme de la situación, contar hasta diez o...)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(978,6,2,'Pido disculpas a quienes he hecho daño (así no haya tenido intención) y logro perdonar cuando me ofenden.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(979,6,2,'Puedo actuar en forma asertiva (es decir, sin agresión pero con claridad y eficacia) para frenar situaciones de abuso en mi vida escolar. (Por ejemplo, cuando se maltrata repetidamente a algún compañero indefenso.)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(980,6,2,'Reconozco cómo se sienten otras personas cuando son agredidas o se vulneran sus derechos y contribuyo a aliviar su malestar.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(981,6,2,'Conozco los derechos fundamentales de los niños y las niñas. (A tener nombre, nacionalidad, familia, cuidado, amor, salud, educación, recreación, alimentación y libre expresión.)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(982,6,2,'Identifico las instituciones y autoridades a las que puedo acudir para pedir la protección y defensa de los derechos de los niños y las niñas y busco apoyo, cuando es necesario.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(983,6,2,'Reconozco el valor de las normas y los acuerdos para la convivencia en la familia, en el medio escolar y en otras situaciones.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(984,6,2,'Reconozco que tengo derecho a mi privacidad e intimidad; exijo el respeto a ello.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(985,6,2,'¡Me cuido a mí mismo! Comprendo que cuidarme y tener hábitos saludables favorece mi bienestar y mis relaciones.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(986,6,2,'Ayudo a cuidarlas plantas, los animales y el medio ambiente en mi entorno cercano.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(987,6,2,'Conozco y sé usar los mecanismos de participación estudiantil de mi medio escolar.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(988,6,2,'Conozco las funciones del gobierno escolar y el manual de convivencia.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(989,6,2,'Identifico y expreso, con mis propias palabras, las ideas y los deseos de quienes participamos en la toma de decisiones, en el salón y en el medio escolar.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(990,6,2,'Expreso, en forma asertiva, mis puntos de vista e intereses en las discusiones grupales.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(991,6,2,'Identifico y manejo mis emociones, como el temor a participar o la rabia, durante las discusiones grupales. (Busco fórmulas secretas para tranquilizarme).',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(992,6,2,'Propongo distintas opciones cuando tomamos decisiones en el salón y en la vida escolar.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(993,6,2,'Coopero y muestro solidaridad con mis compañeros y mis compañeras; trabajo constructivamente en equipo.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(994,6,2,'Participo con mis profesores, compañeros y compañeras en proyectos colectivos orientados al bien común y a la solidaridad.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(995,6,2,'Reconozco que todos los niños y las niñas somos personas con el mismo valor y los mismos derechos.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(996,6,2,'Reconozco lo distintas que somos las personas y comprendo que esas diferencias son oportunidades para construir nuevos conocimientos y relaciones y hacer que la vida sea más interesante y divertida.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(997,6,2,'Identifico mi origen cultural y reconozco y respeto las semejanzas y diferencias con el origen cultural de otra gente. (Al salón llegó una niña de otro lado: habla distinto y me enseña nuevas palabras.)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(998,6,2,'Identifico algunas formas de discriminación en mi escuela (por género, religión, etnia, edad, cultura, aspectos económicos o sociales, capacidades o limitaciones individuales) y colaboro con acciones, normas o acuerdos para evitarlas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(999,6,2,'Identifico mis sentimientos cuando me excluyen o discriminan y entiendo lo que pueden sentir otras personas en esas mismas situaciones.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1000,6,2,'Expreso empatía (sentimientos parecidos o compatibles con los de otros) frente a personas excluidas o discriminadas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1001,6,2,'Identifico y reflexiono acerca de las consecuencias de la discriminación en las personas y en la convivencia escolar.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1002,6,3,'Conozco procesos y técnicas de mediación de conflictos.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1003,6,3,'Sirvo de mediador en conflictos entre compañeros y compañeras, cuando me autorizan, fomentando el diálogo y el entendimiento.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1004,6,3,'Apelo a la mediación escolar, si considero que necesito ayuda para resolver conflictos.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1005,6,3,'Reconozco el conflicto como una oportunidad para aprender y fortalecer nuestras relaciones.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1006,6,3,'Identifico las necesidades y los puntos de vista de personas o grupos en una situación de conflicto, en la que no estoy involucrado. (En un problema, escucho a cada cual para entender sus opiniones.)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1007,6,3,'Comprendo que las intenciones de la gente, muchas veces, son mejores de lo que yo inicialmente pensaba; también veo que hay situaciones en las que alguien puede hacerme daño sin intención.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1008,6,3,'Comprendo que el engaño afecta la confianza entre las personas y reconozco la importancia de recuperar la confianza cuando se ha perdido.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1009,6,3,'Comprendo la importancia de brindar apoyo a la gente que está en una situación difícil. (Por ejemplo, por razones emocionales, económicas, de salud o sociales.)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1010,6,3,'Comprendo que todas las familias tienen derecho al trabajo, la salud, la vivienda, la propiedad, la educación y la recreación.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1011,6,3,'Reflexiono sobre el uso del poder y la autoridad en mi entorno y expreso pacíficamente mi desacuerdo cuando considero que hay injusticias.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1012,6,3,'Comprendo la importancia de los derechos sexuales y reproductivos y analizo sus implicaciones en mi vida. (Por ejemplo, el derecho a la planificación familiar.)',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1013,6,3,'Promuevo el respeto a la vida, frente a riesgos como ignorar señales de tránsito, portar armas, conducir a alta velocidad o habiendo consumido alcohol; sé qué medidas tomar para actuar con responsabilidad frente a un accidente.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1014,6,3,'Comprendo que el espacio público es patrimonio de todos y todas y por eso lo cuido y respeto.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1015,6,3,'Reconozco que los seres vivos y el medio ambiente son un recurso único e irrepetible que merece mi respeto y consideración.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1016,6,3,'Conozco la Declaración Universal de los Derechos Humanos y su relación con los derechos fundamentales enunciados en la Constitución.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1017,6,3,'Conozco los mecanismos constitucionales que protegen los derechos fundamentales (como la tutela) y comprendo cómo se aplican.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1018,6,3,'Analizo el manual de convivencia y las normas de mi institución; las cumplo voluntariamente y participo de manera pacífica en su transformación cuando las considero injustas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1019,6,3,'Exijo el cumplimiento de las normas y los acuerdos por parte de las autoridades, de mis compañeros y de mí mismo/a.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1020,6,3,'Manifiesto indignación (rechazo, dolor, rabia) cuando se vulneran las libertades de las personas y acudo a las autoridades apropiadas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1021,6,3,'Analizo cómo mis pensamientos y emociones influyen en mi participación en las decisiones colectivas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1022,6,3,'Identifico decisiones colectivas en las que intereses de diferentes personas están en conflicto y propongo alternativas de solución que tengan en cuenta esos intereses.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1023,6,3,'Preveo las consecuencias que pueden tener, sobre mí y sobre los demás, las diversas alternativas de acción propuestas frente a una decisión colectiva.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1024,6,3,'Escucho y expreso, con mis palabras, las razones de mis compañeros/as durante discusiones grupales, incluso cuando no estoy de acuerdo.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1025,6,3,'Uso mi libertad de expresión y respeto las opiniones ajenas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1026,6,3,'Comprendo que el disenso y la discusión constructiva contribuyen al progreso del grupo.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1027,6,3,'Comprendo la importancia de participar en el gobierno escolar y de hacer seguimiento a sus representantes.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1028,6,3,'Comprendo que, según la Declaración Universal de los Derechos Humanos y la Constitución Nacional, las personas tenemos derecho a no ser discriminadas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1029,6,3,'Reconozco que los derechos se basan en la igualdad de los seres humanos, aunque cada uno sea, se exprese y viva de manera diferente.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1030,6,3,'Reconozco que pertenezco a diversos grupos (familia, colegio, barrio, región, país, etc.) y entiendo que eso hace parte de mi identidad.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1031,6,3,'Respeto y defiendo las libertades de las personas: libertad de expresión, de conciencia, de pensamiento, de culto y de libre desarrollo de la personalidad.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1032,6,3,'Comprendo que existen diversas formas de expresar las identidades (por ejemplo, la apariencia física, la expresión artística y verbal, y tantas otras...) y las respeto.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1033,6,3,'Comprendo que cuando las personas son discriminadas, su autoestima y sus relaciones con los demás se ven afectadas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1034,6,3,'Identifico mis emociones ante personas o grupos que tienen intereses o gustos distintos a los míos y pienso cómo eso influye en mi trato hacia ellos.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1035,6,3,'Analizo de manera crítica mis pensamientos y acciones cuando estoy en una situación de discriminación y establezco si estoy apoyando o impidiendo dicha situación con mis acciones u omisiones.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1036,6,3,'Actúo con independencia frente a situaciones en las que favorecer a personas excluidas puede afectar mi imagen ante el grupo.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1037,6,3,'Reconozco que los niños, las niñas, los ancianos y las personas discapacitadas merecen cuidado especial, tanto en espacios públicos como privados.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1038,6,4,'Entiendo la importancia de mantener expresiones de afecto y cuidado mutuo con mis familiares, amigos, amigas y pareja, a pesar de las diferencias, disgustos o conflictos.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1039,6,4,'Comprendo que los conflictos ocurren en las relaciones, incluyendo las de pareja, y que se pueden manejar de manera constructiva si nos escuchamos y comprendemos los puntos de vista del otro.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1040,6,4,'Identifico y supero emociones, como el resentimiento y el odio, para poder perdonar y reconciliarme con quienes he tenido conflictos.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1041,6,4,'Utilizo mecanismos constructivos para encauzar mi rabia y enfrentar mis conflictos. (Ideas: detenerme y pensar; desahogarme haciendo ejercicio o hablar con alguien).',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1042,6,4,'Preveo las consecuencias, a corto y largo plazo, de mis acciones y evito aquellas que pueden causarme sufrimiento o hacérselo a otras personas, cercanas o lejanas.',true);
INSERT INTO public.actions(id,area_id,cycleMEN_id,name,is_active) VALUES(1043,6,4,'Conozco y utilizo estrategias creativas para solucionar conflictos. (Por ejemplo, la lluvia de ideas.)',true);
