INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (192,'Asume una posición crítica y propositiva frente a los medios de comunicación masiva para analizar su influencia en la sociedad actual.',10,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (193,'Planea la producción de textos audiovisuales en los que articula elementos verbales y no verbales de la comunicación para desarrollar un tema o una historia.',10,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (194,'Caracteriza la literatura en un momento particular de la historia desde el acercamiento a sus principales exponentes, textos, temáticas y recursos estilísticos.',10,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (195,'Formula puntos de encuentro entre la literatura y las artes plásticas y visuales.',10,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (196,'Participa en discursos orales en los que evalúa aspectos relacionados con la progresión temática, manejo de la voz, tono, estilo y puntos de vista sobre temas sociales, culturales, políticos y científicos.',10,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (197,'Comprende diversos tipos de texto, asumiendo una actitud crítica y argumentando sus puntos de vista frente a lo leído.',10,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (198,'Produce textos orales como ponencias, comentarios, relatorías o entrevistas, atendiendo a la progresión temática, a los interlocutores, al propósito y a la situación comunicativa.',10,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (199,'Escribe textos que evidencian procedimientos sistemáticos de corrección lingüística y el uso de estrategias de producción textual.',10,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (200,'Participa en escenarios académicos, políticos y culturales; asumiendo una posición crítica y propositiva frente a los discursos que le presentan los distintos medios de comunicación y otras fuentes de información.',11,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (201,'Expresa, con sentido crítico, cómo se articulan los códigos verbales y no verbales en diversas manifestaciones humanas y da cuenta de sus implicaciones culturales, sociales e ideológicas.',11,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (202,'Determina los textos que desea leer y la manera en que abordará su comprensión, con base en sus experiencias de formación e inclinaciones literarias.',11,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (203,'Identifica, en las producciones literarias clásicas, diferentes temas que le permiten establecer comparaciones con las visiones de mundo de otras épocas.',11,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (204,'Comprende que los argumentos de sus interlocutores involucran procesos de comprensión, crítica y proposición.',11,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (205,'Compara diversos tipos de texto, con capacidad crítica y argumentativa para establecer relaciones entre temáticas, características y los múltiples contextos en los que fueron producidos.',11,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (206,'Expresa por medio de producciones orales el dominio de un tema, un texto o la obra de un autor.',11,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (207,'Produce textos académicos a partir de procedimientos sistemáticos de corrección lingüística, atendiendo al tipo de texto y al contexto comunicativo.',11,4,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (208,'Comprende que los sentidos le permiten percibir algunas características de los objetos que nos rodean (temperatura, sabor, sonidos, olor, color, texturas y formas).',1,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (209,'Comprende que existe una gran variedad de materiales y que éstos se utilizan para distintos fines, según sus características (longitud, dureza, flexibilidad, permeabilidad al agua, solubilidad, ductilidad, maleabilidad, color, sabor, textura).',1,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (210,'Comprende que los seres vivos (plantas y animales) tienen características comunes (se alimentan, respiran, tienen un ciclo de vida, responden al entorno) y los diferencia de los objetos inertes.',1,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (211,'Comprende que su cuerpo experimenta constantes cambios a lo largo del tiempo y reconoce a partir de su comparación que tiene características similares y diferentes a las de sus padres y compañeros.',1,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (212,'Comprende que una acción mecánica (fuerza) puede producir distintas deformaciones en un objeto, y que este resiste a las fuerzas de diferente modo, de acuerdo con el material del que está hecho.',2,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (213,'Comprende que las sustancias pueden encontrarse en distintos estados (sólido, líquido y gaseoso).',2,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (214,'Comprende la relación entre las características físicas de plantas y animales con los ambientes en donde viven, teniendo en cuenta sus necesidades básicas (luz, agua, aire, suelo, nutrientes, desplazamiento y protección).',2,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (215,'Explica los procesos de cambios físicos que ocurren en el ciclo de vida de plantas y animales de su entorno, en un período de tiempo determinado.',2,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (216,'Comprende la forma en que se propaga la luz a través de diferentes materiales (opacos, transparentes como el aire, translúcidos como el papel y reflectivos como el espejo).',3,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (217,'Comprende la forma en que se produce la sombra y la relación de su tamaño con las distancias entre la fuente de luz, el objeto interpuesto y el lugar donde se produce la sombra.',3,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (218,'Comprende la naturaleza (fenómeno de la vibración) y las características del sonido (altura, timbre, intensidad) y que este se propaga en distintos medios (sólidos, líquidos, gaseosos).',3,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (219,'Comprende la influencia de la variación de la temperatura en los cambios de estado de la materia, considerando como ejemplo el caso del agua.',3,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (220,'Explica la influencia de los factores abióticos (luz, temperatura, suelo y aire) en el desarrollo de los factores bióticos (fauna y flora) de un ecosistema.',3,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (221,'Comprende las relaciones de los seres vivos con otros organismos de su entorno (intra e interespecíficas) y las explica como esenciales para su supervivencia en un ambiente determinado.',3,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (222,'Comprende que la magnitud y la dirección en que se aplica una fuerza puede producir cambios en la forma como se mueve un objeto (dirección y rapidez).',4,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (223,'Comprende los efectos y las ventajas de utilizar máquinas simples en diferentes tareas que requieren la aplicación de una fuerza.',4,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (224,'Comprende que el fenómeno del día y la noche se debe a que la Tierra rota sobre su eje y en consecuencia el sol sólo ilumina la mitad de su superficie.',4,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (225,'Comprende que las fases de la Luna se deben a la posición relativa del Sol, la Luna y la Tierra a lo largo del mes.',4,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (226,'Comprende que existen distintos tipos de mezclas (homogéneas y heterogéneas) que de acuerdo con los materiales que las componen pueden separarse mediante diferentes técnicas (filtración, tamizado, decantación, evaporación).',4,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (227,'Comprende que los organismos cumplen distintas funciones en cada uno de los niveles tróficos y que las relaciones entre ellos pueden representarse en cadenas y redes alimenticias.',4,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (228,'Comprende que existen distintos tipos de ecosistemas (terrestres y acuáticos) y que sus características físicas (temperatura, humedad, tipos de suelo, altitud) permiten que habiten en ellos diferentes seres vivos.',4,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (229,'Comprende que un circuito eléctrico básico está formado por un generador o fuente (pila), conductores (cables) y uno o más dispositivos (bombillos, motores, timbres), que deben estar conectados apropiadamente (por sus dos polos) para que funcionen y produzcan diferentes efectos.',5,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (230,'Comprende que algunos materiales son buenos conductores de la corriente eléctrica y otros no (denominados aislantes) y que el paso de la corriente siempre genera calor.',5,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (231,'Comprende que los sistemas del cuerpo humano están formados por órganos, tejidos y células y que la estructura de cada tipo de célula está relacionada con la función del tejido que forman.',5,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (232,'Comprende que en los seres humanos (y en muchos otros animales) la nutrición involucra el funcionamiento integrado de un conjunto de sistemas de órganos: digestivo, respiratorio y circulatorio.',5,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (233,'Comprende cómo los cuerpos pueden ser cargados eléctricamente asociando esta carga a efectos de atracción y repulsión.',6,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (234,'Comprende que la temperatura (T) y la presión (P) influyen en algunas propiedades fisicoquímicas (solubilidad, viscosidad, densidad, puntos de ebullición y fusión) de las sustancias, y que estas pueden ser aprovechadas en las técnicas de separación de mezclas.',6,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (235,'Comprende la clasificación de los materiales a partir de grupos de sustancias (elementos y compuestos) y mezclas (homogéneas y heterogéneas).',6,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (236,'Comprende algunas de las funciones básicas de la célula (transporte de membrana, obtención de energía y división celular) a partir del análisis de su estructura.',6,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (237,'Comprende la clasificación de los organismos en grupos taxonómicos, de acuerdo con el tipo de células que poseen y reconoce la diversidad de especies que constituyen nuestro planeta y las relaciones de parentesco entre ellas.',6,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (238,'Comprende las formas y las transformaciones de energía en un sistema mecánico y la manera como, en los casos reales, la energía se disipa en el medio (calor, sonido).',7,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (239,'Explica cómo las sustancias se forman a partir de la interacción de los elementos y que estos se encuentran agrupados en un sistema periódico.',7,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (240,'Comprende que en las cadenas y redes tróficas existen flujos de materia y energía, y los relaciona con procesos de nutrición, fotosíntesis y respiración celular.',7,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (241,'Comprende la relación entre los ciclos del carbono, el nitrógeno y del agua, explicando su importancia en el mantenimiento de los ecosistemas.',7,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (242,'Comprende el funcionamiento de máquinas térmicas (motores de combustión, refrigeración) por medio de las leyes de la termodinámica (primera y segunda ley).',8,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (243,'Comprende que en una reacción química se recombinan los átomos de las moléculas de los reactivos para generar productos nuevos, y que dichos productos se forman a partir de fuerzas intramoleculares (enlaces iónicos y covalentes).',8,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (244,'Comprende que el comportamiento de un gas ideal está determinado por las relaciones entre Temperatura (T), Presión (P), Volumen (V) y Cantidad de sustancia (n).',8,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (245,'Analiza relaciones entre sistemas de órganos (excretor, inmune, nervioso, endocrino, óseo y muscular) con los procesos de regulación de las funciones en los seres vivos.',8,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (246,'Analiza la reproducción (asexual, sexual) de distintos grupos de seres vivos y su importancia para la preservación de la vida en el planeta.',8,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (247,'Comprende que el movimiento de un cuerpo, en un marco de referencia inercial dado, se puede describir con gráficos y predecir por medio de expresiones matemáticas.',9,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (248,'Comprende que la acidez y la basicidad son propiedades químicas de algunas sustancias y las relaciona con su importancia biológica y su uso cotidiano e industrial.',9,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (249,'Analiza las relaciones cuantitativas entre solutos y solventes, así como los factores que afectan la formación de soluciones.',9,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (250,'Comprende la forma en que los principios genéticos mendelianos y post-mendelianos explican la herencia y el mejoramiento de las especies existentes.',9,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (251,'Explica la forma como se expresa la información genética contenida en el –ADN–, relacionando su expresión con los fenotipos de los organismos y reconoce su capacidad de modificación a lo largo del tiempo (por mutaciones y otros cambios), como un factor determinante en la generación de diversidad del planeta y en la evolución de las especies.',9,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (252,'Analiza teorías científicas sobre el origen de las especies (selección natural y ancestro común) como modelos científicos que sustentan sus explicaciones desde diferentes evidencias y argumentaciones.',9,1,true);
