INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (254,'Comprende la conservación de la energía mecánica como un principio que permite cuantificar y explicar diferentes fenómenos mecánicos: choques entre cuerpos, movimiento pendular, caída libre, deformación de un sistema masa-resorte.',10,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (255,'Comprende que los diferentes mecanismos de reacción química (oxido-reducción, descomposición, neutralización y precipitación) posibilitan la formación de compuestos inorgánicos.',10,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (256,'Comprende que la biotecnología conlleva el uso y manipulación de la información genética a través de distintas técnicas (fertilización asistida, clonación reproductiva y terapéutica, modificación genética, terapias génicas), y que tiene implicaciones sociales, bioéticas y ambientales.',10,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (257,'Comprende la naturaleza de la propagación del sonido y de la luz como fenómenos ondulatorios (ondas mecánicas y electromagnéticas, respectivamente).',11,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (258,'Comprende que la interacción de las cargas en reposo genera fuerzas eléctricas y que cuando las cargas están en movimiento genera fuerzas magnéticas.',11,1,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (259,'Comprende las relaciones entre corriente y voltaje en circuitos resistivos sencillos en serie, en paralelo y mixtos.',11,1,true);
