INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (261,'Analiza cuestiones ambientales actuales, como el calentamiento global, contaminación, tala de bosques y minería, desde una visión sistémica (económico, social, ambiental y cultural).',11,1,true);
