INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (263,'Describe las características del paisaje geográfico del barrio, vereda o lugar donde vive, sus componentes y formas.',1,2,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (264,'Describe el tiempo personal y se sitúa en secuencias de eventos propios y sociales.',1,2,true);
