INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (266,'Reconoce su individualidad y su pertenencia a los diferentes grupos sociales.',1,2,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (267,'Comprende cambios en las formas de habitar de los grupos humanos, desde el reconocimiento de los tipos de vivienda que se encuentran en el contexto de su barrio, vereda o lugar donde vive.',1,2,true);
