INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (274,'Analiza las actividades económicas de su entorno y el impacto de estas en la comunidad.',2,2,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (275,'Compara las características de las viviendas de su municipio, vereda o lugar donde vive con las de otros lugares.',2,2,true);
