INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (279,'Relaciona las características biogeográficas de su departamento, municipio, resguardo o lugar donde vive, con las actividades económicas que en ellos se realizan.',3,2,true);
INSERT INTO public.DBAS (id,name,grade_id,area_id,is_active) VALUES (280,'Explica las acciones humanas que han incidido en las transformaciones del territorio asociadas al número de habitantes e infraestructura, en su departamento, municipio, resguardo o lugar donde vive.',3,2,true);
