INSERT INTO public.dbasevidences (id,dba_id,name,is_active) VALUES(1348,409,'Identifica el propósito de textos orales y escritos de mediana longitud relacionados con temas de interés general y de su entorno académico y la comparte con otros. Para esto, utiliza su conocimiento acerca de la estructura de los textos.',true);
INSERT INTO public.dbasevidences (id,dba_id,name,is_active) VALUES(1349,410,'Redacta textos argumentativos con una estructura clara y sencilla sobre temas académicos. Para esto, sigue modelos y un proceso de planeación, escritura, revisión y edición; se apoya en su profesor(a) y/o compañeros(as) de clase.',true);
INSERT INTO public.dbasevidences (id,dba_id,name,is_active) VALUES(1350,411,'Expresa de manera oral y escrita su posición acerca de un tema conocido teniendo en cuenta a quién está dirigido el texto. Para esto, define el tono del mensaje de acuerdo con el contexto o la cercanía con su audiencia (formal o informal).',true);
INSERT INTO public.dbasevidences (id,dba_id,name,is_active) VALUES(1351,412,'Expresa de manera oral su punto de vista acerca de un tema controversial previamente estudiado. Para esto, consulta fuentes confiables, selecciona y organiza la información y la utiliza para desarrollar sus ideas de una manera clara y estructurada.',true);
INSERT INTO public.dbasevidences (id,dba_id,name,is_active) VALUES(1352,413,'Mantiene discusiones formales acerca de temas académicos que ha preparado previamente. Para esto, sigue las ideas de un tema propuesto, expresa su opinión y la contrasta con la de otros.',true);
INSERT INTO public.dbasevidences (id,dba_id,name,is_active) VALUES(1353,414,'Narra de manera oral o escrita experiencias personales o historias conocidas. Para esto, brinda detalles de las situaciones, los lugares, las personas y las acciones.',true);
